import React, { useState, useEffect } from 'react';
import { Sun, Moon } from 'phosphor-react'; // Import Phosphor Icons
import TodoList, { Todo } from '../components/TodoList';

const Home: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>(() => {
    const savedTodos = localStorage.getItem('todos');
    return savedTodos ? JSON.parse(savedTodos) : [];
  });
  const [inputValue, setInputValue] = useState('');
  const [darkMode, setDarkMode] = useState(false);

  const addTodo = () => {
    if (inputValue.trim() === '') return;
    const newTodo: Todo = { id: Date.now(), text: inputValue };
    const updatedTodos = [...todos, newTodo];
    setTodos(updatedTodos);
    setInputValue('');
    localStorage.setItem('todos', JSON.stringify(updatedTodos));
  };

  const removeTodo = (id: number) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
    localStorage.setItem('todos', JSON.stringify(updatedTodos));
  };

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      addTodo();
    }
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark', !darkMode);
  };

  return (
    <div className={`flex flex-col items-center justify-center min-h-screen p-6 ${darkMode ? 'bg-gray-900' : 'bg-white'}`}>
      <div className="w-full max-w-md bg-white dark:bg-gray-800 rounded-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-center text-gray-800 dark:text-gray-100">Todo List</h1>
          <button
            onClick={toggleDarkMode}
            className="px-4 py-2 text-sm font-semibold text-gray-800 dark:text-gray-100 bg-gray-200 dark:bg-gray-700 rounded-lg shadow hover:bg-gray-300 dark:hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-400 dark:focus:ring-gray-500"
          >
            {darkMode ? (
              <Sun size={24} weight="bold" /> // Phosphor Sun Icon
            ) : (
              <Moon size={24} weight="bold" /> // Phosphor Moon Icon
            )}
          </button>
        </div>
        <div className="flex mb-4">
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
            className="flex-1 rounded-lg p-2 text-gray-700 dark:text-gray-100 bg-gray-100 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-offset-2 hover:bg-gray-200 dark:hover:bg-gray-600"
            placeholder="Add a new todo"
          />
          <button
            onClick={addTodo}
            className="ml-2 px-4 py-2 text-sm font-semibold text-white bg-black rounded-lg shadow hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-gray-600 focus:ring-offset-2"
          >
            Add
          </button>
        </div>
        <TodoList todos={todos} onRemove={removeTodo} />
      </div>
    </div>
  );
};

export default Home;
