import React, { useState } from 'react';
import TodoItem from './TodoItem';

export interface Todo {
  id: number;
  text: string;
  description?: string;
  completed?: boolean; // New property to track completion
}

interface TodoListProps {
  todos: Todo[];
  onRemove: (id: number) => void;
  onSelect: (todo: Todo) => void;
  isCondensed: boolean; // New prop for list style
}

const TodoList: React.FC<TodoListProps> = ({ todos, onRemove, onSelect, isCondensed }) => {
  const [removing, setRemoving] = useState<number | null>(null);

  const handleRemove = (id: number) => {
    setRemoving(id); // Trigger the animation
    setTimeout(() => {
      onRemove(id); // Remove the item after the animation
      setRemoving(null);
    }, 300); // Match the animation duration
  };

  return (
    <ul className="space-y-2">
      {todos.map((todo) => (
        <li
          key={todo.id}
          className={`transition-all duration-300 ${
            removing === todo.id ? 'opacity-0 translate-x-4' : ''
          }`}
        >
          <TodoItem
            text={todo.text}
            description={todo.description}
            onComplete={() => handleRemove(todo.id)}
            onClick={() => onSelect(todo)}
            isCondensed={isCondensed} // Pass the state to TodoItem
          />
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
