import React, { useState, useEffect } from 'react';
import TodoList, { Todo } from '../components/TodoList';

const Home: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>(() => {
    // Retrieve todos from local storage on initial load
    const savedTodos = localStorage.getItem('todos');
    return savedTodos ? JSON.parse(savedTodos) : [];
  });
  const [inputValue, setInputValue] = useState('');

  const addTodo = () => {
    if (inputValue.trim() === '') return;
    const newTodo: Todo = { id: Date.now(), text: inputValue };
    const updatedTodos = [...todos, newTodo];
    setTodos(updatedTodos);
    setInputValue('');
    localStorage.setItem('todos', JSON.stringify(updatedTodos)); // Save to local storage
  };

  const removeTodo = (id: number) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
    localStorage.setItem('todos', JSON.stringify(updatedTodos)); // Update local storage
  };

  useEffect(() => {
    // Sync todos with local storage whenever they change
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      addTodo();
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white p-6">
      <div className="w-full max-w-md bg-white rounded-lg p-6">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-6">Todo List</h1>
        <div className="flex mb-4">
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
            className="flex-1 rounded-lg p-2 text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-offset-2 hover:bg-gray-100"
            placeholder="Add a new todo"
          />
          <button
            onClick={addTodo}
            className="ml-2 px-4 py-2 text-sm font-semibold text-white bg-gradient-to-r from-green-500 to-green-600 rounded-lg shadow hover:from-green-600 hover:to-green-700 focus:outline-none focus:ring-2 focus:ring-green-400 focus:ring-offset-2"
          >
            Add
          </button>
        </div>
        <TodoList todos={todos} onRemove={removeTodo} />
      </div>
    </div>
  );
};

export default Home;
