import React from 'react';

interface TodoItemProps {
  text: string;
  onComplete: () => void;
}

const TodoItem: React.FC<TodoItemProps> = ({ text, onComplete }) => {
  return (
    <div className="flex items-center justify-between p-4 bg-white shadow-md rounded-lg">
      <span className="text-lg font-medium text-gray-800">{text}</span>
      <button
        onClick={onComplete}
        className="px-4 py-2 text-sm font-semibold text-white bg-gradient-to-r from-blue-500 to-blue-600 rounded-lg shadow hover:from-blue-600 hover:to-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-offset-2"
      >
        Complete
      </button>
    </div>
  );
};

export default TodoItem;
