import React, { useState, useEffect } from 'react';
import { Sun, Moon, List } from 'phosphor-react'; // Import Phosphor Icons
import TodoList, { Todo } from '../components/TodoList';

const Home: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>(() => {
    const savedTodos = localStorage.getItem('todos');
    return savedTodos ? JSON.parse(savedTodos) : [];
  });
  const [inputValue, setInputValue] = useState('');
  const [darkMode, setDarkMode] = useState(true); // Set dark mode as default
  const [sidebarOpen, setSidebarOpen] = useState(true); // State for sidebar toggle
  const [selectedTodo, setSelectedTodo] = useState<Todo | null>(null); // State for selected todo

  useEffect(() => {
    // Ensure the dark mode class is applied on initial load
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const addTodo = () => {
    if (inputValue.trim() === '') return;
    const newTodo: Todo = { id: Date.now(), text: inputValue };
    const updatedTodos = [...todos, newTodo];
    setTodos(updatedTodos);
    setInputValue('');
    localStorage.setItem('todos', JSON.stringify(updatedTodos));
  };

  const removeTodo = (id: number) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
    localStorage.setItem('todos', JSON.stringify(updatedTodos));
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      addTodo();
    }
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const openTodoDetails = (todo: Todo) => {
    setSelectedTodo(todo); // Set the selected todo
  };

  const closeTodoDetails = () => {
    setSelectedTodo(null); // Clear the selected todo
  };

  return (
    <div className={`flex min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-white'}`}>
      {/* Sidebar */}
      <div
        className={`${
          sidebarOpen ? 'w-64' : 'w-16'
        } bg-gray-200 dark:bg-gray-800 transition-all duration-300 flex flex-col p-4`}
      >
        {/* Sidebar Header */}
        <div className="flex items-center justify-between w-full mb-6">
          {sidebarOpen && (
            <h1 className="text-xl font-bold text-gray-800 dark:text-gray-100 transition-all duration-300">
              ZakList
            </h1>
          )}
          <button
            onClick={toggleSidebar}
            className="text-gray-800 dark:text-gray-100 focus:outline-none"
          >
            <List size={24} weight="bold" />
          </button>
        </div>

        {/* Sidebar Content */}
        <div className="flex-1"></div> {/* Empty space to push the toggle to the bottom */}

        {/* Dark Mode Toggle */}
        <button
          onClick={toggleDarkMode}
          className="p-2 text-gray-800 dark:text-gray-100 bg-gray-300 dark:bg-gray-700 rounded-lg shadow hover:bg-gray-400 dark:hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-400 dark:focus:ring-gray-500 w-auto"
        >
          {darkMode ? <Sun size={24} weight="light" /> : <Moon size={24} weight="light" />}
        </button>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col items-center justify-center p-6">
        <div className="w-full max-w-md bg-white dark:bg-gray-800 rounded-lg p-6">
          <div className="flex mb-4">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyDown={handleKeyDown}
              className="flex-1 rounded-lg p-4 text-lg text-gray-700 dark:text-gray-100 bg-gray-100 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-offset-2 hover:bg-gray-200 dark:hover:bg-gray-600"
              placeholder="Add a new todo"
            />
            <button
              onClick={addTodo}
              className="ml-4 px-6 py-4 text-lg font-semibold text-white bg-blue-500 dark:bg-blue-700 rounded-lg shadow hover:bg-blue-600 dark:hover:bg-blue-800 focus:outline-none focus:ring-2 focus:ring-blue-400 dark:focus:ring-blue-600 focus:ring-offset-2"
            >
              Add
            </button>
          </div>
          <TodoList todos={todos} onRemove={removeTodo} onSelect={openTodoDetails} />
        </div>
      </div>

      {/* Right Drawer */}
      {selectedTodo && (
        <>
          {/* Backdrop */}
          <div
            onClick={closeTodoDetails}
            className="fixed inset-0 bg-black bg-opacity-50 z-40"
          ></div>

          {/* Drawer */}
          <div
            className="fixed top-0 right-0 h-full w-64 bg-gray-100 dark:bg-gray-800 shadow-lg p-4 z-50 transition-transform transform translate-x-0 duration-200"
          >
            <button
              onClick={closeTodoDetails}
              className="mb-4 text-gray-800 dark:text-gray-100 focus:outline-none"
            >
              Close
            </button>
            <h2 className="text-xl font-bold text-gray-800 dark:text-gray-100 mb-4">Todo Details</h2>
            <p className="text-gray-700 dark:text-gray-300">{selectedTodo.text}</p>
          </div>
        </>
      )}
    </div>
  );
};

export default Home;
