import React from 'react';

interface TodoItemProps {
  text: string;
  description?: string; // Optional description
  onComplete: () => void;
  onClick: () => void; // Handler for clicking the todo
}

const TodoItem: React.FC<TodoItemProps> = ({ text, description, onComplete, onClick }) => {
  return (
    <div
      onClick={onClick}
      className="flex flex-col items-start justify-between p-4 bg-white dark:bg-gray-800 shadow-md rounded-lg transition-all duration-200 hover:bg-gray-100 dark:hover:bg-gray-700 cursor-pointer"
    >
      <div className="flex items-center justify-between w-full">
        <span className="text-lg font-medium text-gray-800 dark:text-gray-100">{text}</span>
        <button
          onClick={(e) => {
            e.stopPropagation(); // Prevent triggering the parent onClick
            onComplete();
          }}
          className="p-2 text-gray-500 hover:text-green-500 rounded-full focus:outline-none focus:ring-2 focus:ring-green-400 focus:ring-offset-2"
          aria-label="Complete"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={2}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" />
          </svg>
        </button>
      </div>
      {description && (
        <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">{description}</p>
      )}
    </div>
  );
};

export default TodoItem;
