import React from 'react';
import TodoItem from './TodoItem';

export interface Todo {
  id: number;
  text: string;
  description?: string; // Add description property
}

interface TodoListProps {
  todos: Todo[];
  onRemove: (id: number) => void;
  onSelect: (todo: Todo) => void; // New prop for selecting a todo
}

const TodoList: React.FC<TodoListProps> = ({ todos, onRemove, onSelect }) => {
  return (
    <ul className="space-y-2">
      {todos.map((todo) => (
        <li key={todo.id}>
          <TodoItem
            text={todo.text}
            onComplete={() => onRemove(todo.id)}
            onClick={() => onSelect(todo)} // Pass the selected todo
          />
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
